import React, { useState, useEffect } from 'react';
import { createTask, getTasks, deleteTask } from '../services/taskService'; // Import the deleteTask function
import '../assets/css/taskForm.css';

function TaskListComponent() {
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');

  useEffect(() => {
    fetchTasks();
  }, []);

  async function fetchTasks() {
    try {
      const data = await getTasks();
      setTasks(data);
    } catch (error) {
      console.error(error.message);
    }
  }

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      // Send a POST request to the backend to create a new task
      await createTask({ title, description });

      // Clear the form fields after successful submission
      setTitle('');
      setDescription('');

      // Refresh tasks after adding a new task
      fetchTasks();
    } catch (error) {
      console.error('Error adding task:', error);
    }
  };

  const handleDelete = async (id) => {
    try {
      // Send a DELETE request to the backend to delete the task
      await deleteTask(id);

      // Refresh tasks after deleting the task
      fetchTasks();
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };

  return (
    <div>
      <h2>Add New Task</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Title</label>
          <input
            type="text"
            value={title}
            onChange={e => setTitle(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Description</label>
          <input
            type="text"
            value={description}
            onChange={e => setDescription(e.target.value)}
            required
          />
        </div>
        <button type="submit">Add Task</button>
      </form>
      
      <h2>Tasks List</h2>
      {tasks && tasks.length > 0 ? (
        <ul>
          {tasks.map(task => (
            <li key={task.id}>
              {task.title} - {task.description}
              <button onClick={() => handleDelete(task.id)}>Delete</button> {/* Add a Delete button */}
            </li>
          ))}
        </ul>
      ) : (
        <p>No tasks available</p>
      )}
    </div>
  );
}

export default TaskListComponent;
