import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Redirect } from 'react-router-dom';
import SignUpPage from './components/signUpPage';
import TaskListComponent from './components/TaskListComponent';
import './assets/css/App.css';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleLogin = () => {
    // Logic to handle user login (e.g., setting isLoggedIn state to true)
    setIsLoggedIn(true);
  };

  const handleLogout = () => {
    // Logic to handle user logout (e.g., setting isLoggedIn state to false)
    setIsLoggedIn(false);
  };

  return (
    <Router>
      <div className="form-container">
        <h1>Task Manager</h1>
        <Route
          path="/signup"
          render={() => (
            <SignUpPage
              isLoggedIn={isLoggedIn}
              onLogin={handleLogin}
            />
          )}
        />
        <Route
          path="/tasks"
          render={() => (
            isLoggedIn ? (
              <TaskListComponent onLogout={handleLogout} />
            ) : (
              <Redirect to="/signup" />
            )
          )}
        />
      </div>
    </Router>
  );
}

export default App;
