import React from 'react';
import { BrowserRouter as Router, Route, Switch, Redirect } from 'react-router-dom'; // Import Redirect
import TaskListComponent from './components/TaskListComponent';
import TaskFormComponent from './components/TaskFormComponent';
import './assets/css/App.css'; 

function App() {
  return (
    <div className="form-container">
      <h1>Task Manager</h1>
      <Router>
        <Switch>
          <Route exact path="/">
            <Redirect to="/tasks" /> {/* Use Redirect */}
          </Route>
          <Route path="/tasks" component={TaskListComponent} />
          <Route path="/add-task" component={TaskFormComponent} />
        </Switch>
      </Router>
    </div>
  );
}

export default App;
