// TaskFormComponent.js

import React, { useState } from 'react';
import { createTask } from '../services/taskService';
import '../assets/css/taskForm.css';

function TaskFormComponent({ refreshTasks }) {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      // Send a POST request to the /addTasks endpoint with the task data
      await createTask({ title, description });
      
      // Clear the form fields after successful submission
      setTitle('');
      setDescription('');

      // Refresh tasks after adding a new task
      refreshTasks(); // Call the refreshTasks function passed via props
    } catch (error) {
      console.error('Error adding task:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Add New Task</h2>
      <div>
        <label>Title</label>
        <input
          type="text"
          value={title}
          onChange={e => setTitle(e.target.value)}
          required
        />
      </div>
      <div>
        <label>Description</label>
        <input
          type="text"
          value={description}
          onChange={e => setDescription(e.target.value)}
          required
        />
      </div>
      <button type="submit">Add Task</button>
    </form>
  );
}

export default TaskFormComponent;
